import React, { useState } from "react";

const Detalhe = () => {
  // Aqui devemos pegar os dados do formulário para podermos mostrá-lo em a visualização.

  const [nameValue, setNameValue] = useState("");
  const [lastNameValue, setLastNameValue] = useState("");
  const [emailValue, setEmailValue] = useState("");

  const [pokemonName, setPokemonName] = useState("");

  return (
    <div className="detalhe-formulario">
      <div className="cabecalho">
        <h3>Vista prévia da solicitação</h3>
      </div>

      <section className="dados-cliente">
        <h4>Dados do Treinador</h4>

        <div className="lista">
          <p onChange={(e) => setNameValue(e.target.value)} value={nameValue}>
            Nome:
          </p>

          <p>Sobrenome:</p>

          <p onChange={(e) => setEmailValue(e.target.value)} value={emailValue}>
            Email:
          </p>
        </div>
      </section>

      <section className="dados-cliente">
        <h4>Dados do Pokémon</h4>
        <div className="lista">
          <p>Nome:</p>
        </div>
      </section>

      <button
        className="botao-enviar"
        onClick={() => alert("Solicitação enviada :)")}
      >
        Enviar Solicitação
      </button>
    </div>
  );
};

export default Detalhe;
