import React, { useState } from "react";
import { usePokedex } from "../../context/contextoFormulario";

const Detalhe = () => {
  const { formState } = usePokedex;

  return (
    <div className="detalhe-formulario">
      <div className="cabecalho">
        <h3>Vista prévia da solicitação</h3>
      </div>

      <section className="dados-cliente">
        <h4>Dados do Treinador</h4>

        <div className="lista">
          <p>Nome:</p>

          <p>Sobrenome:</p>

          <p>Email:</p>
        </div>
      </section>

      <section className="dados-cliente">
        <h4>Dados do Pokémon</h4>
        <div className="lista">
          <p>Nome:</p>
        </div>
      </section>

      <button
        className="botao-enviar"
        onClick={() => alert("Solicitação enviada :)")}
      >
        Enviar Solicitação
      </button>
    </div>
  );
};

export default Detalhe;
